	"data" : {
		"type" : "disbursements",
		"attributes" : {
			"account-id" : "{{account-id}}",
			"funds-transfer-method-id" : "{{funds-transfer-method-id}}",
			"amount" : "1000",
			"reference" : "MMXYZ123" 
		}
	}
}
